package hello.mapper;import hello.entity.User;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;import org.springframework.stereotype.Component;@Mapper@Componentpublic interface UserMapper {    @Select("SELECT * FROM user WHERE id = #{id}")    User getUserById(@Param("id") Integer id);    @Select("SELECT * FROM user where username = #{username}")    User findUserByUsername(@Param("username") String username);    @Select("INSERT INTO user(username, encrypted_password, created_at, updated_at) values(#{username}, #{password}, NOW(), NOW())")    Integer save(@Param("username") String username, @Param("password") String password);}